#!/usr/bin/env bash
#
# Script to set colors generated by 'wal'
# https://github.com/dylanaraps/wal
 # Source generated colors.
. "${HOME}/.cache/wal/colors.sh"

 reload_dunst() {
    pkill dunst
    dunst \
        -geom "0x0-10+44"\
	-align "center"\
        -lb "${color0}" \
        -nb "${color0}" \
        -cb "${color0}" \
        -lf "${color2}" \
        -bf "${color2}" \
        -cf "#eb4034" \
        -nf "${color2}" &
}

reload_dunst
